mapper.update:
UPDATE reservation
SET status=#{reservationStatus}
WHERE reservation_id = #{reservationId}

mapper.select:
SELECT reservation_id, status, created_datetime, updated_datetime, customer_id, schedule_id
FROM reservation
WHERE reservation_id = #{reservationId};

SELECT reservation_id, status, created_datetime, updated_datetime, customer_id, schedule_id
FROM reservation
LIMIT #{offset}, #{size};
