mapper.select:
SELECT schedule_id, start_datetime, sequence, seats_count, performance_id, created_datetime, updated_datetime
FROM schedule
WHERE schedule_id = #{scheduleId};

SELECT EXISTS(SELECT schedule_id
              FROM schedule
              WHERE schedule_id = #{scheduleId});

mapper.update:
UPDATE schedule
SET seats_count = #{seatsCount}
WHERE schedule_id = #{scheduleId}
